import React, { useState, useContext } from "react";
import styled from "styled-components";
import WeatherContent from "./WeatherContent"

import { WeatherStateContext } from "../App";
import { WeatherListprops, LanguageProps } from "../models/weather";



const WeatherInfos = ({ language } : LanguageProps) => {

  const values  = useContext(WeatherStateContext) as WeatherListprops[];
  const [view, setView] = useState('yview');
  const changeListview = () => {
    view === "yview" ? setView("xview") : setView("yview")
  };
  console.log(values)
  return (
    <Weatherlist>
      <Titlefivedays>
        {language === "ENG" ? "5 Day / 3 Hour Forecast" : "5일 / 3시간 예보"}
        <ChangeView onClick={changeListview}>{language === "ENG" ? "change view" : "화면 변환"}</ChangeView>
      </Titlefivedays>
      {view === "yview" ?
        <YweatherContainer>
          {values.map((items, index) => (
            <ContainerList key={index} style={{ marginBottom: 20, width: "33.33%" }} >
              <WeatherContent items={items} language={language} />
            </ContainerList>
          ))}
        </YweatherContainer>
        :
        <XweatherContainer>
          {values.map((items, index) => (
            <ContainerList key={index} style={{ marginLeft: 10, marginRight: 10, width: "33.33%" }} >
              <WeatherContent items={items} language={language} />
            </ContainerList>
          ))}
        </XweatherContainer>
      }
    </Weatherlist>
  )
};

export default WeatherInfos;
const Weatherlist = styled.article`
  ul::-webkit-scrollbar {
    width: 5px; 
  }

  ul::-webkit-scrollbar-thumb {
      height: 30%; 
      background: #217af4;
      border-radius: 10px;
  }

  ul::-webkit-scrollbar-track {
      background: rgba(33, 122, 244, .1);
  }
`

const Titlefivedays = styled.div`
  border-top: 2px solid #fff;
  border-bottom: 2px solid #fff;
  padding : 0 5px;
  color : white;
  box-sizing : border-box;
  font-weight : 700;
  display : flex;
  justify-content : space-between;
  align-items : center;
  height : 40px;
`;

const YweatherContainer = styled.ul`
  display : flex;
  flex-wrap : wrap;
  padding: 0;
  justify-content : space-between;
  overflow-y : scroll;
  max-height : 340px;
   > li {
    list-style: none;
   }
`;


const XweatherContainer = styled.ul`
  display : flex;
  flex-wrap : nowrap;
  overflow-x : auto;
  padding : 0;
  > li {
    list-style: none;
   }
`;

const ContainerList = styled.li` > div {
  color : white;
  text-align : center;
  font-size : 15px;
}
`;

const ChangeView = styled.button`
  border : none;
  height : 40px;
  background : rgba(0,0,0,0.5);
  color : #e5e5e5;
  cursor : pointer;
  max-height : 25px;
  font-family: 'Roboto';
  border-radius : 5px;
`;
